import { useState, useEffect } from "react";
import { Form } from "react-router-dom";
import classes from "./RegisterMainFrame.module.css";

function RegisterMainFrame() {
    const [lastName, setLastName] = useState("");
    const [firstName, setFirstName] = useState("");
    const [account, setAccount] = useState("");
    const [password, setPassword] = useState("");
    const [checkPassword, setCheckPassword] = useState("");
    const [isButtonEnabled, setIsButtonEnabled] = useState(false);
    const [passwordError, setPasswordError] = useState("密碼不符合格式");
    const [checkPasswordError, setCheckPasswordError] = useState("test");

    useEffect(() => {
        if (lastName && firstName && account && password && checkPassword) {
            setIsButtonEnabled(true);
        } else {
            setIsButtonEnabled(false);
        }
    }, [lastName, firstName, account, password, checkPassword]);

    return (
        <Form className={classes.formContainer}>
            <div className={classes.label}>
                <span>姓名</span>
                <span className={classes.starSign}> *</span>
            </div>
            <div className={classes.nameGroup}>
                <div className={classes.lastNameInput}>
                    <input
                        placeholder="姓氏"
                        onChange={(e) => setLastName(e.target.value)}
                    />
                </div>
                <div className={classes.firstNameInput}>
                    <input
                        placeholder="名字"
                        onChange={(e) => setFirstName(e.target.value)}
                    />
                </div>
            </div>
            <div className={classes.label}>
                <span>帳號</span>
                <span className={classes.starSign}> *</span>
            </div>
            <div className={classes.inputGroup}>
                <input
                    placeholder="帳號"
                    onChange={(e) => setAccount(e.target.value)}
                />
            </div>
            <div className={classes.label}>
                <span>密碼</span>
                <span className={classes.starSign}> *</span>
                <span className={classes.errorMessage}>{passwordError}</span>
            </div>
            <div className={classes.inputGroup2}>
                <input
                    placeholder="請輸入8位以上由字母和數字組成的密碼"
                    onChange={(e) => setPassword(e.target.value)}
                />
            </div>
            <div className={classes.errorMessage2}></div>
            <div className={classes.inputGroup}>
                <input
                    placeholder="請再次輸入密碼"
                    onChange={(e) => setCheckPassword(e.target.value)}
                />
            </div>
            <button className={classes.loginButton} disabled={!isButtonEnabled}>
                送出
            </button>
        </Form>
    );
}

export default RegisterMainFrame;
