import { useEffect, useRef } from "react";
import classes from "./NotePadHomePage.module.css";

function NotePadHomePage() {
    const pathRef = useRef(null);

    useEffect(() => {
        const path = pathRef.current;
        if (path) {
            const length = path.getTotalLength();
            // 設定 stroke-dasharray 和 stroke-dashoffset
            path.style.strokeDasharray = length;
            path.style.strokeDashoffset = length;

            const glowPath = document.querySelector(".glow");
            if (glowPath) {
                glowPath.style.strokeDasharray = length;
                glowPath.style.strokeDashoffset = length;
            }
        }
    }, []);

    return (
        <div className="notePadContainer">
            <div className="header">
                <div className="title">記事本</div>
            </div>
            <svg width="100%" height="200">
                {/* 光線效果 */}
                <path
                    ref={pathRef}
                    d="M0,100 C150,10 350,190 490,100"
                    className="glow"
                    fill="none"
                />
                {/* 主線條 */}
                <path
                    ref={pathRef}
                    d="M0,100 C150,10 350,190 490,100"
                    className="line"
                    fill="none"
                />
            </svg>
        </div>
    );
}

export default NotePadHomePage;
