import { useState, useRef, useEffect } from "react";
import classes from "./MessageInput.module.css";
import sendButton from "../../images/sendButton.png";

function MessageInput() {
    const [text, setText] = useState("");

    const textareaRef = useRef(null);
    const containerRef = useRef(null);

    function textChangeHandler(e) {
        setText(e.target.value);
    }

    function keyDownHandler(e) {
        if (e.key === "Enter" && !e.shiftKey) {
            e.preventDefault(); // 阻止默認行為 (也就是不要在textarea創建新行)
        }
    }

    useEffect(() => {
        const textarea = textareaRef.current;
        const container = containerRef.current;

        // 自適應高度
        textarea.style.height = "auto";
        textarea.style.height = `${textarea.scrollHeight}px`;

        // 當達到設定高度時，變成滾動，停止增加高度
        if (textarea.scrollHeight > 140) {
            textarea.style.height = "140px";
            textarea.style.overflowY = "scroll";
        } else {
            textarea.style.overflowY = "hidden";
        }

        // 根據內容調整容器高度
        container.style.width = "auto";
    }, [text]);

    return (
        <>
            <div className={classes.textareaContainer} ref={containerRef}>
                <textarea
                    className={classes.customTextarea}
                    placeholder="發送訊息"
                    value={text}
                    ref={textareaRef}
                    rows="1"
                    onChange={textChangeHandler}
                    onKeyDown={keyDownHandler}
                />
            </div>
            <button className={classes.sendButton}>
                <img src={sendButton} alt="送出" width="45px" height="45px" />
            </button>
        </>
    );
}

export default MessageInput;
