import { useState } from "react";
import def_avatar from "../../images/default_avatar.png";
import classes from "./ContactPerson.module.css";

function ContactPerson({ name, personality, photo, isSelected, onClick }) {
    const [showModal, setShowModal] = useState(false);

    const handleRightClick = (event) => {
        event.preventDefault(); // 阻止預設右鍵選單
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
    };

    async function handleConfirmDelete() {
        setShowModal(false);

        const formData = new FormData();
        formData.append("model_original_name", name);
        formData.append("anticipation", personality);
        try {
            const access_token = localStorage.getItem("accessToken");
            const response = await fetch(
                "https://nccu-group-8.work/userinfo/user/create_model",
                {
                    method: "POST",
                    headers: {
                        "Authorization": "Bearer " + access_token,
                    },
                    body: formData,
                }
            );

            if (response.status === 200) {
                setShowModal(false);
                console.log("刪除模型成功");
                window.location.reload();
            } else if (response.status === 400) {
                const responseData = await response.json();
                const error = responseData.error;
                console.log("錯誤訊息: " + error);
            } else {
                const responseData = await response.json();
                const error = responseData.message;
                console.error(error);
                alert("發生錯誤，請重新整理後再試一次");
            }
        } catch (error) {
            console.error("Error durning create model: ", error);
        }
    };

    return (
        <div
            className={`${classes.container} ${
                isSelected ? classes.isSelected : ""
            } ${!showModal ? classes.disableInteraction : ""}`}
            onClick={onClick}
            onContextMenu={handleRightClick}
        >
            <img src={photo} alt="模型頭像" className={classes.avatar} />
            <div className={classes.content}>
                <div className={classes.name}>姓名： {name}</div>
                <div className={classes.personality}>
                    個性留言： {personality}
                </div>
            </div>
            {showModal && (
                <div className={classes.modalBackdrop} onClick={handleCloseModal}>
                    <div className={classes.modal} onClick={(event) => event.stopPropagation()}>
                        <p>是否要刪除此模型</p>
                        <div className={classes.modalButtons}>
                            <button
                                className={classes.cancelButton}
                                onClick={handleCloseModal}
                            >
                                否
                            </button>
                            <button
                                className={classes.confirmButton}
                                onClick={handleConfirmDelete}
                            >
                                是
                            </button>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default ContactPerson;
