import { useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import EventCard from "./EventCard";
import goBack_white from "../../images/goBack_white.png";
import add_event from "../../images/add_event.png";
import nextImg from "../../images/goBack_white.png";
import classes from "./CheckEvent.module.css";

function CheckEvent() {
    const [currentPage, setCurrentPage] = useState(1);

    const eventList = [
        { id: 1, name: "平台", personality: "預設" },
        { id: 2, name: "子安", personality: "測試" },
        { id: 3, name: "ZK", personality: "測試2" },
        { id: 3, name: "ZK", personality: "測試2" },
    ];

    const eventsPerPage = 3; // 每頁顯示的事件數量
    const totalPages = Math.ceil(eventList.length / eventsPerPage); // 總頁數

    const navigate = useNavigate();

    const { time } = useParams();

    function goBackHandler() {
        navigate("/notepad");
    }

    function handleNextPage() {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
        }
    }

    function handlePreviousPage() {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    }

    const startIndex = (currentPage - 1) * eventsPerPage;
    const currentEvents = eventList.slice(startIndex, startIndex + eventsPerPage);

    return (
        <div className={classes.checkEventContainer}>
            <div className={classes.header}>
                <img
                    src={goBack_white}
                    className={classes.goBack}
                    alt="返回"
                    onClick={goBackHandler}
                />
                <div className={classes.title}>記事本</div>
            </div>
            <div className={classes.mainContent}>
                <div className={classes.contentHeader}>
                    <div className={classes.contentTitle} data-text={time}>{time}</div>
                    <img src={add_event} alt="新增事件" className={classes.createEventBtn} />
                </div>
                <div className={classes.eventList}>
                    {currentPage > 1 && <img src={nextImg} alt="上一個" className={classes.leftImg} onClick={handlePreviousPage} />}
                    {currentEvents.map((event, index) => (
                        <EventCard
                            key={index}
                            index={startIndex + index + 1}
                        />
                    ))}
                    {currentPage < totalPages && <img src={nextImg} alt="下一個" className={classes.rightImg} onClick={handleNextPage} />}
                </div>
                {eventList.length === 0 && <div className={classes.noEvent}>尚無事件紀錄</div>}
            </div>
        </div>
    );
}

export default CheckEvent;
