import { useEffect } from "react";
import classes from "./NotePadHomePage.module.css";

function NotePadHomePage() {
    const AnimatedLine = () => {
        useEffect(() => {
            // 你可以在這裡控制動畫的啟動或結束
        }, []);
    };

    return (
        <div className={classes.notePadContainer}>
            <div className={classes.header}>
                <div className={classes.title}>記事本</div>
            </div>
            <svg width="100%" height="200" viewBox="0 0 100 100" preserveAspectRatio="none">
                <path
                    id="curve"
                    d="M10,100 C150,10 350,190 490,100"
                    className="glow"
                    fill="none"
                />
                <path
                    id="curve"
                    d="M10,100 C150,10 350,190 490,100"
                    className="line"
                    fill="none"
                />
            </svg>
        </div>
    );
}

export default NotePadHomePage;
