import { useEffect } from "react";
import classes from "./NotePadHomePage.module.css";

function NotePadHomePage() {
    const AnimatedLine = () => {
        useEffect(() => {
            // 你可以在這裡控制動畫的啟動或結束
        }, []);
    };

    return (
        <svg width="100%" height="200">
        {/* 光線的動畫部分 */}
        <path
          id="glowCurve"
          d="M10,100 C150,10 350,190 490,100"
          className="glow-line"  // 用 glow-line 來實現光線效果
          fill="none"
        />
  
        {/* 主線條的動畫部分 */}
        <path
          id="mainCurve"
          d="M10,100 C150,10 350,190 490,100"
          className="main-line"  // 用 main-line 來顯現線條
          fill="none"
        />
      </svg>
    );
}

export default NotePadHomePage;
