import { useState, useEffect, useRef } from "react";
import { useParams, useLocation, redirect } from "react-router-dom";
import ChatRoomHeader from "./ChatRoomHeader";
import UserMessage from "./UserMessage";
import BotMessage from "./BotMessage";
import ChooseResponse from "./ChooseResponse";
import MessageInput from "./MessageInput";
import question from "../../images/question.png";
import { inference } from "../../utils/modelUtils";
import { refresh } from "../../utils/tokenUtils";
import classes from "./MessageList.module.css";

function MessageList({ dummy, dummyName }) {
    const [messages, setMessages] = useState([]);
    const [pageSize, setPageSize] = useState(false);

    // Áî®ÊñºÂÑ≤Â≠òÂâç‰∫îÁ≠ÜÂ∞çË©±Á¥ÄÈåÑ
    const [conversationHistory, setConversationHistory] = useState([]);

    const [showInfoModal, setShowInfoModal] = useState(false);

    const [pendingChoices, setPendingChoices] = useState(null);
    const [loadingHint, setLoadingHint] = useState(false);
    const messagesEndRef = useRef(null);

    useEffect(() => {
        // Áï∂ÁµÑ‰ª∂È¶ñÊ¨°Âä†ËºâÊôÇËß∏ÁôºÊ∑°ÂÖ•ÊïàÊûú
        setPageSize(dummy);
    }, [dummy]);

    useEffect(() => {
        // ÊªëÂà∞Â∫ïÈÉ®
        scrollToBottom();
    }, [messages, pendingChoices]);

    const { id } = useParams();
    const location = useLocation();
    const { person } = location.state || {};

    const name = person ? person.model_original_name : dummyName;
    const modelname = person ? person.modelname : "";
    const isShared = person ? person.is_shared : "";

    async function sendMessageHandler(userMessage) {
        setMessages([...messages, { type: "user", text: userMessage }]);
        setLoadingHint(true);

        console.log(conversationHistory);

        const formData = new FormData();
        formData.append("is_shared", String(isShared));
        formData.append("modelname", modelname);
        formData.append("input_text", userMessage);
        formData.append("session_history", JSON.stringify(conversationHistory));

        try {
            const response = await inference(formData);
            const accessToken = localStorage.getItem("accessToken");

            if (response.status === 200) {
                const responseData = await response.json();
                const results = responseData.result;
                if (results.length === 1) {
                    // Âè™Êúâ‰∏ÄÂÄãÂõûÁ≠îÊôÇ
                    const botOutput = results[0].output;
                    setLoadingHint(false);
                    setMessages((prevMessages) => [
                        ...prevMessages,
                        { type: "bot", text: botOutput },
                    ]);
                    updateConversationHistory({
                        "user": userMessage,
                        "model": botOutput,
                    });

                  } else if (results.length > 1) {
                    // ÊúâÂÖ©ÂÄãÊàñÊõ¥Â§öÂõûÁ≠îÊôÇ
                    let botOutputs = []
                    botOutputs[0] = results[0].output;
                    botOutputs[1] = results[1].output;
                    setLoadingHint(false);
                    setPendingChoices(botOutputs);
                    console.log(conversationHistory);
                }


            } else if (response.status === 400) {
                const responseData = await response.json();
                const error = responseData.error;
                console.log("ÈåØË™§Ë®äÊÅØ: " + error);
            } else if (response.status === 401 && accessToken) {
                // access TokenÈÅéÊúüÔºåÁî®refresh TokenÂéªÊãøÊñ∞ÁöÑaccess Token
                const checkReTokenStatus = await refresh();
                if (checkReTokenStatus) {
                    const response = await inference(formData);
                    if (response.status === 200) {
                        const responseData = await response.json();
                        const results = responseData.result;
                        if (results.length === 1) {
                            // Âè™Êúâ‰∏ÄÂÄãÂõûÁ≠îÊôÇ
                            const botOutput = results[0].output;
                            setLoadingHint(false);
                            setMessages((prevMessages) => [
                                ...prevMessages,
                                { type: "bot", text: botOutput },
                            ]);
                            updateConversationHistory({
                                "user": userMessage,
                                "model": botOutput,
                            });

                          } else if (results.length > 1) {
                            // ÊúâÂÖ©ÂÄãÊàñÊõ¥Â§öÂõûÁ≠îÊôÇ
                            let botOutputs = []
                            botOutputs[0] = results[0].output;
                            botOutputs[1] = results[1].output;
                            setLoadingHint(false);
                            setPendingChoices(botOutputs);
                        }

                    }
                } else {
                    // refresh TokenÈÅéÊúüÔºåÈáçÊñ∞ÁôªÂÖ•‰∏¶Âà™Êéâ localStorage Ë£°ÁöÑÊù±Ë•ø
                    // alert("refresh TokenÈÅéÊúüÔºåË´ãÈáçÊñ∞ÁôªÂÖ•!");
                    localStorage.clear();
                    redirect("/login");
                }
            } else {
                const responseData = await response.json();
                const error = responseData.message;
                console.error(error);
                alert("ÁôºÁîüÈåØË™§ÔºåË´ãÈáçÊñ∞Êï¥ÁêÜÂæåÂÜçË©¶‰∏ÄÊ¨°");
            }
        } catch (error) {
            console.error("Error durning inference model: ", error);
            redirect("/login");
        }

    }

    function updateConversationHistory(newConversation) {
        setConversationHistory((prevHistory) => {
            const updatedHistory = [...prevHistory, newConversation];
            if (updatedHistory.length > 5) {
                updatedHistory.shift(); // Ë∂ÖÈÅé 5 Ê¢ùÊôÇÂà™Èô§ÊúÄËàäÁöÑ
            }
            return updatedHistory;
        });
    }

    function handleChoice(selection) {
        // Ê∑ªÂä†ÈÅ∏ÊìáÁöÑÂõûË¶ÜÁÇ∫ botMessage
        setMessages((prevMessages) => [
            ...prevMessages,
            { type: "bot", text: selection },
        ]);

        updateConversationHistory({
            user: messages[messages.length - 1]?.text || "",
            model: selection,
        });

        // Èö±ËóèÈÅ∏ÊìáÂÖÉ‰ª∂
        setPendingChoices(null);
    }

    function scrollToBottom() {
        // ÊªæÂãïÂà∞Ê∂àÊÅØÂàóË°®Â∫ïÈÉ®
        if (messagesEndRef.current) {
            messagesEndRef.current.scrollIntoView({ behavior: "smooth" });
        }
    }

    function viewInfoModalHandler() {
        setShowInfoModal(true);
    }

    function closeInfoModalHandler() {
        setShowInfoModal(false);
    }


    return (
        <>
            <div className={pageSize ? classes.outerContainerDummy : classes.outerContainer}>
                <ChatRoomHeader name={name} modelName={modelname} viewInfoModal={viewInfoModalHandler} />
                <div className={classes.listContainer}>
                    {messages.map((message, index) =>
                        message.type === "user" ? (
                            <UserMessage key={index} content={message.text} />
                        ) : (
                            <BotMessage key={index} content={message.text} avatar={person.modelphoto} />
                        )
                    )}
                    {pendingChoices && (
                        <ChooseResponse
                            res1={pendingChoices[0]}
                            res2={pendingChoices[1]}
                            onChoose={handleChoice}
                            avatar={person.modelphoto}
                        />
                    )}
                    <div ref={messagesEndRef}></div>
                </div>
                <MessageInput onSendMessage={sendMessageHandler} name={name} loadingHint={loadingHint} disabled={loadingHint} />

                {showInfoModal && (
                <div
                    className={classes.infoModalOverlay}
                    onClick={closeInfoModalHandler}
                >
                    <div
                        className={classes.infoModalContent}
                        onClick={(e) => e.stopPropagation()} // Èò≤Ê≠¢ÈªûÊìäÂÖßÂÆπÂçÄÂüüËß∏ÁôºÈóúÈñâ
                    >
                        <div className={classes.infoHeader}>
                            <img src={question} alt="Êõ¥Â§öË≥áË®ä" className={classes.infoIcon} />
                            <div>Á≥ªÁµ±‰ΩøÁî®Ë™™Êòé üåü</div>
                        </div>
                        <div className={classes.infoMainContent}>
                            <div>
                                <div className={classes.infoTitle2}>‚ùóÂ∞èÊèêÈÜíÔºö</div>
                                <ul className={classes.listStyle2}>
                                    <li>ÂâõÊñ∞Âª∫ÁöÑÊ®°ÂûãÈúÄË¶ÅÂÖàÂà∞„ÄêË®ìÁ∑¥Ë≥áÊñô„Äë‰∏äÂÇ≥Ë≥áÊñô‰∏¶ÂÆåÊàêË®ìÁ∑¥ÔºåÊâçËÉΩË∑üÂ∞àÂ±¨Ê®°ÂûãÊö¢ËÅäÔΩûÂ¶ÇÊûúË∑≥ÈÅéË®ìÁ∑¥ÔºåÁ≥ªÁµ±ÊúÉÁî®Âü∫Á§éÊ®°ÂûãËÅäÂ§©Âì¶</li>
                                    <li>Áî®ÂàÜ‰∫´ÈÄ£ÁµêÂª∫Á´ãÁöÑÊ®°ÂûãÂèØ‰ª•Áõ¥Êé•ÈñãËÅäÔºåÂÆåÂÖ®ÂÖçË®ìÁ∑¥ÔºåË∂ÖÂø´ÈÄüÔºÅüöÄ</li>
                                </ul>
                            </div>
                        </div>
                        <div className={classes.infoBottom}></div>
                    </div>
                </div>
            )}
            </div>
        </>
    );
}

export default MessageList;
