import { useState, useEffect } from "react";
import { useNavigate, useParams, redirect } from "react-router-dom";
import goBack_white from "../../images/goBack_white.png";
import { getOneEvent } from "../../utils/notepadUtils";
import { refresh } from "../../utils/tokenUtils";
import classes from "./EventContentMainFrame.module.css";

function EventContentMainFrame() {
    const [event, setEvent] = useState(null);
    const [loading, setLoading] = useState(true);

    const navigate = useNavigate();
    const { time, id } = useParams();

    function goBackHandler() {
        navigate(`/notepad/event/${time}`);
    }

    useEffect(() => {
        if (!id) return;

        async function fetchEvents() {
            const response = await getOneEvent(id);
            const accessToken = localStorage.getItem("accessToken");

            if (response.status === 200) {
                const responseData = await response.json();
                setEvent(responseData);
            } else if (response.status === 403) {
                setEvent(null);
            } else if (response.status === 401 && accessToken) {
                // access Token過期，用refresh Token去拿新的access Token
                const checkReTokenStatus = await refresh();

                if (checkReTokenStatus) {
                    const response = await getOneEvent(id);

                    if (response.status === 200) {
                        const responseData = await response.json();
                        setEvent(responseData);
                    }
                } else {
                    // refresh Token過期，重新登入並刪掉 localStorage 裡的東西
                    // alert("refresh Token過期，請重新登入!");
                    localStorage.clear();
                    redirect("/login");
                }
            }
            // 發生其他問題
            redirect("/login");
        }

        fetchEvents();
    }, [id]); // 當 `id` 改變時重新呼叫 API

    return (
        <div className={classes.checkEventContainer}>
            <div className={classes.header}>
                <img
                    src={goBack_white}
                    className={classes.goBack}
                    alt="返回"
                    onClick={goBackHandler}
                />
                <div className={classes.title}>記事本</div>
            </div>
            <div className={classes.mainContent}>
                {!loading && (
                    <>
                        <div className={classes.backToNotePadBtn}>
                            回記事本主頁
                        </div>
                        <div className={classes.titleAndImg}>
                            <img alt="事件照片" />
                            <div></div>
                        </div>
                        <div></div>
                    </>
                )}
            </div>
        </div>
    );
}

export default EventContentMainFrame;
