export async function getEvents(time) {
    const accessToken = localStorage.getItem("accessToken");
    let url
    if (time) {
        url = `https://nccu-group-8.work/event/getevents/${time}`
    } else {
        url = "https://nccu-group-8.work/event/getevents";
    }
    const response = await fetch( url,
        {
            method: "GET",
            headers: {
                Authorization: "Bearer " + accessToken,
            },
        }
    );

    return response;
}

export async function createEvent(content, date, photo, title) {
    const accessToken = localStorage.getItem("accessToken");
    const response = await fetch(
        "https://nccu-group-8.work/userinfo/user/create_model",
        {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
                Authorization: "Bearer " + accessToken,
            },
            body: JSON.stringify({
                event_content: "全家一起去日本玩",
                event_date: "2022-12-25",
                event_picture: "event_picture3.jpg",
                event_title: "全家出遊"
            }),
        }
    );

    return response;
}
